package javaPackage;

public class Addition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=10;
		int b=20;
		int sum=a+b;
		
		int sub=a-b;
		
		System.out.println("This is the sum of 2 numbers "+sum+".");
		System.out.println("This is the difference of 2 numbers "+sub+".");
		
		int c=4;
		int d=25;
		int multiply=c*d;
		
		double divide=d/c;
		int mod=d%c;
		
		System.out.println("This is the product of 2 numbers "+multiply+".");
		System.out.println("This is the quotient of 2 numbers "+divide+".");
		System.out.println("The modulus output is "+mod);
		
	}

}
